// unloadScene puzzle
function unloadScene(url) {
    if (appInstance.scene) {
        var scene = url === '' ? appInstance.scene : appInstance.scene.getObjectByName(url);
        if (scene) appInstance.unload(scene);

        if (!appInstance.scene) appInstance.renderer.clear();

        // clean object cache
        _pGlob.objCache = {};
    }
}

// loadScene puzzle
function loadScene(url, sceneName, loadCb, progCb, errorCb) {

    appInstance.unload();

    // clean object cache
    _pGlob.objCache = {};

    _pGlob.percentage = 0;
    appInstance.loadScene(url, function(loadedScene) {
        appInstance.enableControls();
        loadedScene.name = sceneName;

        _pGlob.percentage = 100;
        loadCb();
    }, function(percentage) {
        _pGlob.percentage = percentage;
        progCb();
    }, errorCb);
}

// Describe this function...
function goBack() {
  if (isChangeScene == true) {
    isChangeScene = false;
    unloadScene('Fajas_overland.gltf.xz');
    loadScene('Chancado_area_2600.gltf.xz', 'Chancado_area_2600.gltf.xz', function() {
      endPreloader();
      setActiveCamera('camara_paneo');
      changeVis(operadores_visibles, true);
    }, function() {
      updatePreloader(Math.round(_pGlob.percentage), '');
    }, function() {});
  } else {
    setActiveCamera('camara_paneo');
    changeVis(operadores_visibles, true);
    tweenCamera('operador_paneo', '2600_chancado_pica_rocas', 2, function() {}, 1);
  }
}

